export default (posts = [], action) => {
  switch (action.type) {
    case 'UPDATE':
      return posts.map((post) => post._id === action.paylad._id ? action.payload : post);
    case 'FETCH_ALL':
      return action.payload;
    case 'CREATE':
      return [...posts, action.payload];
    default:
      return posts;
  }
}
